'use strict'

const axios = require('axios');

module.exports.getCalculatedCoinDetails = (req, res,next) => {
    return new Promise((resolve,reject)=>{
        axios.get('https://api.nanopool.org/v1/eth/approximated_earnings/25')
        .then(function (data) {
            return resolve(data)
        })
    })
    .then(values=>{
      const returnedDetails= values.data;
        res.status(200).json(returnedDetails)
       }).catch(error=>{
        res.status(200).json(error)
       })
    
}
