'use strict'
module.exports.getpaymethod=(req,res,next)=>{
    let options = req.headers.options
    ,db = options.db
   

    
    // ,emailconfig = require('../../../../config/emailsend.js')
   ,response = {
    'success':false,
    'message':''     
   
   }
   console.log('The body is',req.body)

    return new Promise((resolve,reject)=>{
    db.query('select get_getway_details()',[] , (err, result) => {
         if(err){
            console.log('The Error',err)
            response['success']= 'false'
            response['message'] = 'Error in Operation'
            return reject(response)

         }else{
            console.log('the row value',result.rows[0].get_getway_details)
            response['success']= true
            response['message'] = 'Data Fetched'
            response['data'] = result.rows[0].get_getway_details
            return resolve(response)

         }

    })
   }).then(response=>{
    res.status(200).json(response)
   }).catch(error=>{
    res.status(200).json(response)
   })

}